    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        String x = accesscode.getText();
        
        if (jRadioButton1.isSelected() == false && jRadioButton2.isSelected() == false && jRadioButton3.isSelected() == false) {
            errorwarning4.setText("Please Select An Option");
        }
                
        if(accesscode.getText().length() < 6 && accesscode.getText().length() >= 1){
            errorwarning.setText("Must Be Six Characters");
        }  
        
        else if(accesscode.getText().trim().isEmpty()){
            errorwarning.setText("Must Enter Access Code");
        }      
                    
            
        else
            if(jRadioButton1.isSelected()){
                System.out.println("Access Code: "+ x);
                wifi.show();
                step1.dispose();
            }
            else
            if(jRadioButton2.isSelected()){
                System.out.println("Access Code: "+ x);
                bluetooth.show();
                step1.dispose();
            }
            else
            if(jRadioButton3.isSelected()){
                System.out.println("Access Code: "+ x);
                nfc.show();
                step1.dispose();
            }           
    }//GEN-LAST:event_jButton9ActionPerformed

